import React, {useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faCircleCheck, faPen, faTrashCan
 } from '@fortawesome/free-solid-svg-icons';

 import './App.css';

function App() {

  const [toDo, setToDo] = useState([
    {"id":1, "baslik": "Görev 1", "durum": false},
    {"id":2, "baslik": "Görev 2", "durum": false},
  ]);

  const [yeniGorev, setYeniGorev] = useState('');
  const [gorevGuncelle, setGorevGuncelle] = useState('');

  const ekleGorev = () => {
      if (yeniGorev) {
        let number = toDo.length + 1;
        let yeniGiris = { id: number, baslik: yeniGorev, durum: false }
        setToDo([...toDo, yeniGiris])
        setYeniGorev('');
      }
  };


  const silGorev = (id) => {
      let yeniGorevler = toDo.filter(gorev => gorev.id !== id)
      setToDo(yeniGorevler);
  };

  const tamamGorev = (id) => {
      let yeniGorev = toDo.map( gorev =>{
        if (gorev.id == id) {
          return ({...gorev, durum: !gorev.durum})
        }
        return gorev;
      })
      setToDo(yeniGorev);
  };

  const iptalGuncel = () => {
      setGorevGuncelle('');
  };

  const duzenleGorev = (e) => {
      let yeniGiris = {
        id: gorevGuncelle.id,
        baslik: e.target.value,
        durum: gorevGuncelle.durum ? true : false
      }
      setGorevGuncelle(yeniGiris);
  };

  const guncelGorev = () => {
    let kayıtFiltresi = [...toDo.filter( gorev => gorev.id !== gorevGuncelle.id )]
  };

  return (
    <div className="container App">
      <h3 className='m-5'>Yapılacaklar Listesi</h3>

        <div className="row mb-3">
            <div className="col">
              <input value={ gorevGuncelle && gorevGuncelle.baslik} onChange={(e) => duzenleGorev(e)} className="form-control form-control-lg" />
            </div>
            <div className="col-auto">
              <button className="btn btn-lg btn-success mr-20" onClick={guncelGorev}>Güncelle</button>
              <button className="btn btn-lg btn-danger">Vazgeç</button>
            </div>
          </div>

        <div className="row mb-3">
            <div className="col">
              <input value={yeniGorev} onChange={ (e) => setYeniGorev(e.target.value)} className="form-control form-control-lg" />
            </div>
            <div className="col-auto">
              <button onClick={ekleGorev} className="btn btn-lg btn-success">Görev Ekle</button>
            </div>
          </div>

          {toDo && toDo.length ? '' : 'Aktif Görev Yok.'}

          {toDo && toDo
            .sort((a, b) => a.id > b.id ? 1: -1)
            .map( (gorev, index) => {
              return(
                <React.Fragment key={gorev.id}>
                  <div className="col gorevBg">
                      <div className={gorev.durum ? 'yapildi' : ''}>
                        <span className="gorevSirasi">{index + 1}</span>
                        <span className="gorevText">{gorev.baslik}</span>
                      </div>
                      <div className="ikonlar">
                          <span title="Yapıldı / Yapılmadı" onClick={ (e) => tamamGorev(gorev.id)}> <FontAwesomeIcon icon={faCircleCheck} /> </span>

                          {gorev.durum ? null :(
                            <span title="Düzenle" onClick={() => setGorevGuncelle({id: gorev.id, baslik: gorev.baslik, durum: gorev.durum ? true : false})}> <FontAwesomeIcon icon={faPen} /> </span>
                          )}

                          <span title="Görevi Sil" onClick={() => silGorev(gorev.id)} > <FontAwesomeIcon icon={faTrashCan} /> </span>
                      </div>
                  </div>  
                </React.Fragment>
          )
        })
      }

    </div>
  );
}

export default App;
